import React, { Component } from "react";

class Todo extends Component {
  state = {
    color: "warning"
  };

  handleColorChange = e => {
    if (e.target.checked) {
      this.setState({
        color: "success"
      });
    } else {
      this.setState({
        color: "warning"
      });
    }
  };

  render() {
    const { color } = this.state;
    const { info, handleTaskComplete, handleRemoveTask } = this.props;
    return (
      <div className={`alert alert-${color}`}>
        <div className="row">
          <div className="col-4">
            <div>{info.desc}</div>
          </div>
          <div className="col-4">
            <div className="d-flex flex-row align-items-center">
              <input
                type="checkbox"
                className="mr-2"
                onChange={e => {
                  handleTaskComplete(info.id, e.target.checked);
                  this.handleColorChange(e);
                }}
              />
              <span>Completed</span>
            </div>
          </div>
          <div className="col-4">
            <button
              type="button"
              className="btn btn-danger"
              onClick={() => handleRemoveTask(info.id)}
            >
              Remove
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default Todo;
