import React, { Component } from "react";

class Todo extends Component {
  state = {
    color: "warning"
  };

  handleColorChange = e => {
    if (e.target.checked) {
      this.setState({
        color: "success"
      });
    } else {
      this.setState({
        color: "warning"
      });
    }
  };

  render() {
    const { color } = this.state;
    const { desc, handleTaskComplete } = this.props;
    return (
      <div className={`alert alert-${color}`}>
        <div>{desc}</div>
        <div className="d-flex flex-row align-items-center">
          <input
            type="checkbox"
            className="mr-2"
            onChange={e => {
              handleTaskComplete();
              this.handleColorChange(e);
            }}
          />
          <span>Completed</span>
        </div>
      </div>
    );
  }
}

export default Todo;
