import React, { Component } from "react";
import "./TodoList.css";
import Todo from "./Components/Todo";

const idNum = 0;
class TodoList extends Component {
  state = {
    itemCount: 0,
    uncheckedCount: 0,
    todos: [],
    task: ""
  };

  handleChange = e => {
    const name = e.target.name;
    const val = e.target.value;
    this.setState({
      [name]: val
    });
  };

  addNewTask = () => {
    const newArr = [];
    let newVal = this.state.task;
    newArr.push({ id: idNum++, desc: newVal, complete: false });
    this.setState({
      todos: newArr
    });
  };

  handleTaskComplete = () => {
    const { uncheckedCount } = this.state;
    let newVal = uncheckedCount + 1;
    this.setState({
      uncheckedCount: newVal
    });
  };

  handleRemoveTask = id => {
    const { todos } = this.state;
    const newArr = todos.filter(todo => {
      if (todo.id !== id) return todo;
    });
    this.setState({ todos: newArr });
  };

  resetForm = () => {
    this.setState({
      task: ""
    });
  };
  render() {
    const { itemCount, uncheckedCount, task, todos } = this.state;
    return (
      <div className="container center">
        <h1 className="center title">My TODO App</h1>
        <div className="flow-right controls">
          <span>Item count: {itemCount}</span>
          <span>Unchecked count: {uncheckedCount}</span>
        </div>
        <button
          className="button center"
          data-toggle="modal"
          data-target="#addTodo"
        >
          New TODO
        </button>
        <div
          className="modal fade"
          id="addTodo"
          role="dialog"
          aria-labelledby="addTodoTitle"
          aria-hidden="true"
        >
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="addTodoTitle">
                  New TODO
                </h5>
                <button
                  type="button"
                  className="close"
                  data-dismiss="modal"
                  aria-label="Close"
                  onClick={() => this.resetForm()}
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <form>
                  <div className="form-group">
                    <label>What do you need to do?</label>
                    <input
                      type="text"
                      className="form-control"
                      name="task"
                      value={task}
                      onChange={this.handleChange}
                    />
                  </div>
                </form>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-dismiss="modal"
                  onClick={() => this.resetForm()}
                >
                  Close
                </button>
                <button
                  type="button"
                  className="btn btn-primary"
                  data-dismiss="modal"
                  onClick={() => this.addNewTask()}
                >
                  Add TODO
                </button>
              </div>
            </div>
          </div>
        </div>
        <div className="todo-list">
          {todos &&
            todos.map(todo => (
              <Todo
                key={todo.id}
                info={todo}
                handleTaskComplete={this.handleTaskComplete}
                handleRemoveTask={this.handleRemoveTask}
              />
            ))}
        </div>
      </div>
    );
  }
}

export default TodoList;
